"""# Inicializando o ambiente"""

import numpy as np
import gymnasium as gym
from gymnasium.envs.toy_text.frozen_lake import generate_random_map

# Inicializar o ambiente
# O ambiente pode ser criado para os seguintes modelos de renderização (parâmetro "render_mode" do método "make"): ansi, human, rgb_array

#env = gym.make('FrozenLake-v1', desc=None, map_name="4x4", is_slippery=True, p=0.8)
env = gym.make('FrozenLake-v1', render_mode="ansi", desc=generate_random_map(size=8), is_slippery=False)

# Fazer o reset para que o agente seja colocado no ponto inicial do ambiente e em seguida fazer a renderização
env.reset()
print(env.render())

#Declare uma tabela Q (quality) com formato np.array com n spaces linhas e n actions colunas.
q_table = np.zeros([env.observation_space.n, env.action_space.n])

#Tamanho da tabela Q
q_table

#Tamanho da tabela Q
q_table.shape

"""# Treinamento do agente"""

import random
from IPython.display import clear_output
import numpy as np

alfa = 0.2
gama = 0.95
epsilon = 1  # Estratégia conhecida na literatura da área como estratégia epsion-greedy. Serve para estabelecer uma probabilidade do agente fazer exploration ou exploitation.
epsilon_min = 0.01  # Valor mínimo de epsilon
epsilon_max = 1
epsilon_decay = 0.00001  # Taxa de decaimento de epsilon (mais agressivo) usar 0.0001
num_episodios = 500000  # (size=4, 50000; size=6, 100000; size=8, 1000000;)

total_penalidades, sucesso = 0, 0
for i in range(1, num_episodios):
  estado, _ = env.reset()
  epocas, penalidades, recompensa,  = 0, 0, 0
  concluido = False

  while not concluido:
    # Exploração -> ação aleatória
    if random.uniform(0 ,1) < epsilon:
      acao = env.action_space.sample()

    # Exploitation -> ação definida na tabela Q
    else:
      acao = np.argmax(q_table[estado])

    prox_estado, recompensa, concluido, _, informacoes = env.step(acao)

    q_antigo = q_table[estado, acao]
    prox_valor_max = np.max(q_table[prox_estado])

    q_novo = (1 - alfa) * q_antigo + alfa * (recompensa + gama * prox_valor_max)
    q_table[estado, acao] = q_novo

    if concluido == True and recompensa == 0:
      total_penalidades += 1
    if concluido == True and recompensa == 1:
      sucesso += 1

    estado = prox_estado
    epocas += 1

  #epsilon = max(epsilon_min, epsilon * epsilon_decay)
  epsilon = epsilon_min + (epsilon_max - epsilon_min) * np.exp(-epsilon_decay * i)

  total_penalidades += penalidades

  if i % 1000 == 0:
    clear_output(wait=True)
    print(f"Episodio: {i}")
    print(f"Epsilon: {epsilon}")

print(f"Treinamento concluido!")
print(f"Total de falhas no treinamento: {total_penalidades}")
print(f"Total de sucesso no treinamento: {sucesso}")

q_table

"""# Teste do agente

* Após treinar o agente e obter a tabela Q é possível testar o agente.

* Execute o algoritmo por n episódios e faça o computo das penalidades.
"""

env.reset()
print(env.render())

episodios = 50
sucesso = 0
total_penalidades = 0
total_epocas = 0

import time
for i in range(episodios):
  estado, _ = env.reset()
  recompensa, epocas = 0, 0
  concluido = False

  while not concluido:
    acao = np.argmax(q_table[estado])
    print(f"Episodio: {i}")
    print(f"Epocas: {epocas}")
    print(f"Acao: {acao}")
    estado, recompensa, concluido, _, informacoes = env.step(acao)

    if concluido == True and recompensa == 0:
      total_penalidades += 1

    if recompensa == 1:
      sucesso+=1;

    epocas += 1

    clear_output(wait=True)
    print(env.render())
    #time.sleep(1)

  total_epocas += epocas

print(f"Número de episodios: {episodios}. Objetivo alcançado {sucesso} vezes.")
print(f"Agente caiu no buraco {total_penalidades} vezes em {total_epocas} epocas.")